<script>

import { mapGetters } from 'vuex'
import { getUserList } from '@/api/user'
import { getArticleList } from '@/api/article'
import { getListByPage } from '@/api/comment'
export default {
  name: 'Dashboard',
  computed: {
    ...mapGetters([
      'name'
    ])
  },
  data(){
    return{
      userTotal:1,
      articleTotal:1,
      commentTotal:1,
    }
  },
  created(){
    this.getUserTotal()
    this. getArticleTotal()
    this.getCommentTotal()
  },
  methods:{
    getUserTotal(){
      const params ={
        pageSize: 10,
        pageNum: 1
      }
      getUserList(params).then(res=>{
        this.userTotal =res.data.total
      })
    },
    getArticleTotal(){
      const params  ={
        pageSize: 10,
        currentPage: 1
      }
      getArticleList(params).then(res=>{
        this.articleTotal = res.data.total
      })
    },
    getCommentTotal(){
      const params  ={
        pagesize: 10,
        currentPage: 1
      }
      getListByPage(params).then(res=>{
        this.commentTotal = res.data.Total
      })
    },
  }
}
</script>

